mapper.insert:
INSERT INTO post (
                    title
                    , is_admin
                    , contents
                    , views
                    , file_id
                    , create_time
                    , user_id
                    , category_id
) VALUES (
                    #{title}
                    , #{isAdmin}
                    , #{contents}
                    , #{views}
                    , #{fileId}
                    , #{createTime}
                    , #{userId}
                    , #{categoryId}
)

mapper.update:
UPDATE
            post
SET
            title = #{title}
            , is_admin = #{isAdmin}
            , contents = #{contents}
            , file_id = #{fileId}
            , user_id = #{userId}
            , category_id = #{categoryId}
            , modify_time = #{modifyTime}
WHERE
            id = #{id}
